from game_info.parser_server import Game, GameParserServer, SearchError


class TestGameObject:

    def test_init(self):
        game = Game("test", 1)
        assert game.name == 'test'
        assert game.score == 1

    def test_equlity(self):
        game_1 = Game("test", 1)
        game_2 = Game("test", 2)
        game_3 = Game("TEST", 2)
        game_4 = Game("not test", 1)

        assert game_1 == game_1
        assert game_1 == game_2
        assert game_1 == game_3
        assert game_1 != game_4

        assert game_1 == "test"
        assert game_1 == u"test"
        assert game_1 != "not test"
        assert game_1 != u"not test"

        assert "test" in [game_1]
        assert game_1 in [game_1]

    def test_compare(self):
        game_1 = Game("test", 10)
        game_2 = Game("test", 1)
        game_3 = Game("TEST", 20)

        assert game_1 > game_2
        assert game_2 < game_3
        assert game_3 > game_1

    def test_representation(self):
        game_1 = Game("test", 10)
        representation = {
            "title": "test",
            "score": 10
              }
        assert game_1.to_dict() == representation
        assert str(game_1) == str(representation)

