class TestParserServer:

    def test_init(self):
        server = GameParserServer(8888, "test")
        assert server.port == 8888
        assert server.url == "test"

    def test_parse(self):
        server = GameParserServer(8888, "test")
        with pytest.raises(SearchError):
            server.parse()

        with patch.object(requests, 'get') as mock_method:
            mock_response = MagicMock()
            mock_response.status_code = 200
            mock_response.text = RESPONSE
            mock_method.return_value = mock_response
            server.parse()

    def test_sorted_games(self):
        server = GameParserServer(8888, "test")
        with patch.object(requests, 'get') as mock_method:
            mock_response = MagicMock()
            mock_response.status_code = 200
            mock_response.text = RESPONSE
            mock_method.return_value = mock_response
            server.parse()
            assert server.sorted_games() != []
            assert len(server.sorted_games()) == 10

    def test_search(self):
        server = GameParserServer(8888, "test")
        with patch.object(requests, 'get') as mock_method:
            mock_response = MagicMock()
            mock_response.status_code = 200
            mock_response.text = RESPONSE
            mock_method.return_value = mock_response
            server.parse()
            assert "Bloodborne" in server.sorted_games()
            assert "bloodborne" in server.sorted_games()
            assert "fail" not in server.sorted_games()
